        /// <summary>
        /// Получение расписания и/или результатов матчей за определенную дату
        /// </summary>
        /// <param name="date">Дата в формате dd.MM.yyyy, матчи которой интересуют</param>
        /// <returns></returns>
        [HttpGet]
        public async Task<IActionResult> SendMatchesToChannel([FromQuery(Name = "date")]string? dateStr = null)
        {
            if (!DateTime.TryParseExact(dateStr, "dd.MM.yyyy", null, DateTimeStyles.None, out DateTime date))
                date = DateTime.Today;

            string html = await calendarHtmlProvider.GetHtmlAsync(date);

            List<MatchInfo> matches = htmlParser.ParseMatches(html, date);

            string msg = MatchesToText(matches, date);

            await sender.SendText(msg);

            return Ok();
        }

        private string MatchesToText(List<MatchInfo> matches, DateTime date)
        {
            // TODO: преобразование текста вынести в отдельные классы
            TimeZoneInfo timeZone = TimeZoneInfo.GetSystemTimeZones().First(x => x.Id == "Russian Standard Time");
            StringBuilder sb = new StringBuilder();
            sb.AppendLine($"<b>МАТЧИ {date:dd MMMM yyyy}</b>");
            sb.AppendLine();

            foreach (var match in matches)
            {
                sb.Append($"<b>{match.Left.Name}</b> ({match.Left.City})");
                sb.Append(" - ");
                sb.Append($"<b>{match.Right.Name}</b> ({match.Right.City})");
                sb.AppendLine();

                if (string.IsNullOrEmpty(match.Result))
                {
                    sb.Append($"В {TimeZoneInfo.ConvertTime(match.DateAndTime, timeZone):HH.mm} МСК");
                }
                else
                {
                    sb.Append($"Результат матча: <span class=\"tg-spoiler\">{match.Result}</span>");
                }

                // TODO: ссылки на трансляции

                sb.AppendLine();
                sb.AppendLine();
            }

            return sb.ToString();
        }
